public class ThreadExample extends Thread {

    @Override
    public void run() {
        System.out.println("Thread bắt đầu chạy");
        try {
            Thread.sleep(1000); // Giả lập công việc mất thời gian
        } catch (InterruptedException e) {
            e.printStackTrace();
        }
        System.out.println("Thread kết thúc");
    }

    public static void main(String[] args) {
        System.out.println("=== Test với start() ===");
        ThreadExample thread1 = new ThreadExample();
        thread1.start(); // Tạo thread mới
        System.out.println("Main thread kết thúc ngay sau khi gọi start()");

        System.out.println("\n=== Test với run() ===");
        ThreadExample thread2 = new ThreadExample();
        thread2.run(); // Chạy trong main thread
        System.out.println("Main thread kết thúc sau khi run() hoàn thành");
    }
}
